import logo from './logo.svg';
import './App.css';
import React, { useEffect, useState } from 'react';
import axios from 'axios';
const Forecast = () => {
  const [forecastData, setForecastData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [latitude, setLatitude] = useState('41.881832');
  const [longitude, setLongitude] = useState('-87.623177');
  useEffect(() => {
    fetchForecastGridData();
  }, []);

  const fetchForecastGridData = async () => {
    try {


      const apiUrl = `https://api.weather.gov/points/${latitude},${longitude}`;
      const response = await axios.get(apiUrl);
      const forecastGridDataUrl = response.data.properties.forecastGridData;
      const forecastGridResponse = await axios.get(forecastGridDataUrl);
      const forecastGrid = forecastGridResponse.data.properties;

      setForecastData(forecastGrid);
      setLoading(false);
    } catch (error) {
      console.error(`Error fetching forecast grid data: ${error}`);
    }
  };
  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
      <h1>Forecast Grid Data</h1>
        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Temperature</th>
              <th>Wind Speed</th>
              <th>Precipitation</th>
            </tr>
          </thead>
          <tbody>
            {forecastData.periods.map((period) => (
              <tr key={period.number}>
                <td>{period.name}</td>
                <td>{period.temperature}</td>
                <td>{period.windSpeed}</td>
                <td>{period.precipitation}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      )}
    </div>
  );
};

export default Forecast;
