import './App.css';
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Weather = () => {
  const [cityName, setCityName] = useState('');
  const [weather, setWeather] = useState('');
  const [latitude, setLatitude] = useState('41.881832');
  const [longitude, setLongitude] = useState('-87.623177');
  useEffect(() => {
    fetchWeatherData(latitude, longitude);
  }, [latitude,longitude]);

  const fetchWeatherData = async (latitude, longitude) => {
    try {
      const apiUrl = `https://api.weather.gov/points/${latitude},${longitude}`;
      const response = await axios.get(apiUrl);
      const city = response.data.properties.relativeLocation.properties.city;
      const forecast = response.data.properties.forecast;
      setCityName(city);
      fetchForecastData(forecast);
    } catch (error) {
      console.error(`Error fetching weather data: ${error}`);
    }
  };

  const fetchForecastData = async (forecastUrl) => {
    try {
      const response = await axios.get(forecastUrl);
      const weatherDescription = response.data.properties.periods[0].detailedForecast;
      setWeather(weatherDescription);
    } catch (error) {
      console.error(`Error fetching forecast data: ${error}`);
    }
  };
  const handleSearch = (e) => {
    e.preventDefault();
    fetchWeatherData(latitude, longitude);
  };
  return (
    <div className="App">
      <header className="App-header">

        <form onSubmit={handleSearch}>
        <label>
          Latitude:
          <input
            type="text"
            value={latitude}
            onChange={(e) => setLatitude(e.target.value)}
          />
        </label>
        <br />
        <label>
          Longitude:
          <input
            type="text"
            value={longitude}
            onChange={(e) => setLongitude(e.target.value)}
          />
        </label>
        <button type="submit">Search</button>
      </form>
        <div>
          <h1>{cityName}</h1>
          <p>{weather }</p>
        </div>
      </header>
    </div>
  );
}

export default Weather;
