import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Forecast = () => {
  const [forecastData, setForecastData] = useState([]);
  const [latitude, setLatitude] = useState('41.881832');
  const [longitude, setLongitude] = useState('-87.623177');
  useEffect(() => {
    fetchForecastData();
  }, []);

  const fetchForecastData = async () => {
    try {
      const apiUrl = `https://api.weather.gov/forecast/hourly/${latitude},${longitude}`;
      const response = await axios.get(apiUrl);
      const forecastData = response.data.properties.periods;
      setForecastData(forecastData);
    } catch (error) {
      console.error(`Error fetching forecast data: ${error}`);
    }
  };

  return (
    <div>
      <h1>Next 5 Days Forecast</h1>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Time</th>
            <th>Conditions</th>
            <th>High</th>
            <th>Low</th>
          </tr>
        </thead>
        <tbody>
          {forecastData.map((forecast) => (
            <tr key={forecast.startTime}>
              <td>{forecast.startTime}</td>
              <td>{forecast.endTime}</td>
              <td>{forecast.weatherSummary}</td>
              <td>{forecast.temperature.high}</td>
              <td>{forecast.temperature.low}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Forecast;
